  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  const getNextOptionalAccount = () => {
    const accountMeta = getNextAccount();
    return accountMeta.address === PANDA_BATTLE_PROGRAM_ADDRESS
      ? undefined
      : accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      admin: getNextAccount(),
      validator: getNextOptionalAccount(),
      bufferGameRound: getNextAccount(),
      delegationRecordGameRound: getNextAccount(),
      delegationMetadataGameRound: getNextAccount(),
      gameRound: getNextAccount(),
      ownerProgram: getNextAccount(),
      delegationProgram: getNextAccount(),
      systemProgram: getNextAccount(),
    },
    data: getDelegateRoundInstructionDataDecoder().decode(instruction.data),
  };
